$(document).ready(function() {

    var action = "search";

    $("#search").click(function() {

        var search_mssv = $("#inputmssv").val();

        $.ajax({

            url: "Home/Search",

            method: "POST",

            data: {

                action: action,

                search_mssv: search_mssv

            },

            success: function(data) {

                $("#output_search").html(data);

            }

        });

    });

});

</script>



